//b-tag scale factor
bool applySF (bool isBTagged, float Btag_SF, float Btag_eff)
{
  bool newBTag = bool(isBTagged);
  float mistagPercent;

  if (Btag_SF == 1) return newBTag; //no correction needed                                                                                                                                                                        
  //throw dice
  TRandom3 rand2(0);
  double coin  = rand2.Uniform(0,1.0);

  if (Btag_SF > 1){  //use this if SF>1
    if (!isBTagged){
      //fraction of jets that need to be upgraded
      mistagPercent = (1.0 - Btag_SF) / (1.0 - (1.0/Btag_eff) );
      //upgrade to tagged
      if ( coin < mistagPercent ) newBTag = true;
    }
  }
  else{  //use this if SF<1
    //downgrade tagged to untagged
    if ( isBTagged && coin > Btag_SF ){
      newBTag = false;
    }
  }
  return bool(newBTag);
}



// float puppiCorr = getPUPPIweight( AK8jetPt , AK8jetEta );
// float jetmass = AK8PUPPISoftdropUncorrectedMass*puppiCorr;
float getPUPPIweight(float puppipt, float puppieta , TF1* puppisd_corrGEN  , TF1* puppisd_corrRECO_cen, TF1* puppisd_corrRECO_for ){

  float genCorr  = 1.;
  float recoCorr = 1.;
  float totalWeight = 1.;

  genCorr =  puppisd_corrGEN->Eval( puppipt );
  if( fabs(puppieta)  <= 1.3 ){
    recoCorr = puppisd_corrRECO_cen->Eval( puppipt );
  }
  else{
    recoCorr = puppisd_corrRECO_for->Eval( puppipt );
  }

  totalWeight = genCorr * recoCorr;

  return totalWeight;
}
